INSERT INTO `employee_position` VALUES (30, 'Reservation Officer', '<p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">JOB DESCRIPTION:&nbsp;</span></b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;color:#333333\">Reservation Agent<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">REPORTS TO:&nbsp;</span></b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;color:#333333\">Reservation Manager or Front Office Manager<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">POSITION SUMMARY:</span></b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;color:#333333\"><o:p></o:p></span></p><p class=\"MsoNorma