INSERT INTO `employee_position` VALUES (55, 'Room Service Order Taker', '<p>&nbsp; &nbsp;&nbsp;<b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;color:#333333;mso-font-kerning:18.0pt\">Job Description for\r\nRoom Service Order Taker</span></b><br></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">Position Title:</span></b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;color:#333333\">&nbsp;Room Service Order Taker / In Room\r\nDining (IRD) Order Taker&nbsp;<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">Reports To:</span></b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;color:#333333\">&nbsp;room service manager <o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">Position Summary:</span></b><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;color:#333333\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">As a Room Service\r\nOrder Taker you are responsible to co-ordinate all service requirements\r\naddressed to Room Service department, primarily responsible for guest orders\r\nfrom room or pool area via phone or any other ordering devices like mobile\r\napps, TV etc.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size:12.0pt;font-family:&quot;Tahoma&quot;,sans-serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">Also punches the food\r\nand beverage orders on to the Point of Sales systems like Micros POS. He / she\r\nshould also coordinate guest amenities and organizes guest table pick-ups on\r\ncompletion of meals in a timely manner.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: ini