INSERT INTO `standard_operation_procedure_set_up` VALUES (4, '01. Telephone Etiquette in hotels', 1, '2019-10-02', 1, '2019-10-02', 1, '<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Date August 2019 </span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Telephone Etiquette in hotels</span></span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">SOP Number:</span></span></strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;FO-01 (SOP serial number and department code)</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Department:</span></span></strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;Front Office - General</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Basic Telephone Etiquettes:</span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">What all points should be taken care of while answering a call?</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Phone should be answered within three rings.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Smile even though you are on the telephone.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Sit or stand up straight.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Use a low voice pitch.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Match your speaking rate to the caller&#39;s.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Avoid extremes in volume.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Standard Phrase:</span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">How to answer an External call?</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Wish according to the time of the day (Good Morning, Afternoon or Evening), (Your Hotel Name), How may I help you.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">How to answer Internal Guest calls?</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Wish according to the time of the day (Good Morning, Afternoon or Evening) Mr. _________ (Guest last name), This is ________(Your first name), How may I help you.</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&q