INSERT INTO `standard_operation_procedure_set_up` VALUES (21, '18. How to waitlist reservation in hotels', 1, '2019-10-03', 1, NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Date August 2019 </span></span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">How to waitlist reservation in hotels</span></span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">SOP Number: FO- 19 (SOP serial number and department code)</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Department: Front Office - Reservations</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Wait list reservation:</span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">At some times a reservation request must be denied because the hotel is fully booked. However, with enough lead time, interested guests may be put on a wait listing list for the requested dates.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Hotels experiencing high-volume reservations may satisfy excess demand through this technique.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">1) Answer the call with a Smile:</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Hi, this is &ldquo;(your name)&rdquo;&rsquo; at room reservations. How may I help you?</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Guest: I would like to make a room reservation please.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">2) Offer to take the guest&rsquo;s name and Phone number</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Certainly Sir/Mam. (Pause) May I request you for your name, mobile number and email ID please?</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">3) And your company/travel agency name is?</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">I am from &ldquo;(Company/TA name)&rdquo;.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">4) The arrival date of the guest is? (Pause, take answer) And the departure date is? (Pause, take answer). So that is for &ldquo;(# of nights)&rdquo; nights?</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Yes.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">5) Please hold for a moment, while I check the availability. (Put the call on hold, check availability and go back to the call quickly)</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">6) Convey the non-availability of room politely to the guest.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Mr./Ms. &ldquo;(caller&rsquo;s name)&rdquo;, currently we will have to waitlist the reservation as we do not have any rooms available and are sold out for these dates.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">7) Agree to notify the prospective guest immediately if a room becomes available on the requested dates.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Please allow me to get back to you in &ldquo;(estimated time/day to call back the caller)&rdquo;. Thank you</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">8) Help the guest to find alternative dates if no rooms are available.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Training Summary questions:</span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q1. When do we waitlist a room reservation? &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q2. What all details to be collected from the guests? &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q3. How to convey the non-availability of room to the guest? &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q4. Is it required to get back to the guest when the rooms are available on requested date?</span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n');
INSERT INTO `standard_operation_procedure_set_up` VALUES (22, '19 Handling Wake Up Call', 1, '2019-10-03', 1, NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Date August 2019 </span></span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">SOP Number:</span></span></span></strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;FO - 20 (SOP serial number and department code)</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Department:</span></span></span></strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;Front Office - General</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">It is very important to handle wakeup call accurately and promptly for guests in hotel font desk operations, If Front desk staffs fails to give the wakeup call to guests then this can lead to inconvenience to guest e.g.: Missing flights or important meetings and lead to guest dissatisfaction.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Taking down Wakeup Call for guests:</span></span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Automated process:&nbsp;</span></span></span></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">In most of the hotels guests can set wakeup call from their room telephone, hotel mobile application or in-room entertainment system (T.V)</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Proper instructions must be displayed on the telephone instrument, TV screen or on a tent card next to the telephone or in-room entertainment system.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">The procedure of setting the wake-up call from the room must be well aware to all front desk staff, in case the guest asks for the same then the staff should be able to give clear instruction.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Once the guest enter the wakeup call from his room the information will be updated to the EPBX system and also to the front desk property management system (If an interface is available between telephone system and property management software)</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Even if guest had set the wakeup call on his own from the in room telephone, the front desk or guest service staff should always give the guest a reminder call manually.</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Manual Process:</span></span></span></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Guest can also give the wakeup call request directly to the front office staff at the reception or calling from the room.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Greet the guest as per the time.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Take down the below details on to the wake- up call log book / wakeup call register:</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Guest Name</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Room Number</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Wake up date</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Wake up time</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Any special request ( e.g.: Tea / Coffee with wakeup call)</span></span></span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Repeat the details written with the guest again for re-confirmation.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Wish the guest a pleasant continuation when leaving the counter or at the end of the call. &nbsp;(E.g.: Good night, have a nice day ahead etc.)</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Call the Room service and inform them about the special request if any. Note down the name of the Room service staff to whom the information was passed.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Update the details on the PMS (property management software) by accessing the reservation record for this room. (This step may vary according to the property management system the hotel is using.)</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Cross check the details entered on the PMS with what is written on the wakeup call log book.</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Performing Wake-up calls:</span></span></span></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Print &lsquo;wakeup call report&rsquo; sorted by time from the PMS for the required date.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Cross check with the wake-up call log book and make sure that there is nothing missing or not updated on the system.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Automated Wakeup call or 1st wakeup call would be performed by the EPBX automatically.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">The Reminder wakeup call or follow up call or 2nd Wake-up call need to be performed by the Front desk staff or the telephone operator.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Once the 2nd wakeup call is completed ticket mark that reservation on the report and update the remarks section on the report.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">If the Guest does not answer to the 2nd reminder call then try again after 5 minutes.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Security department to be informed in case the below:</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Guest doesn&rsquo;t respond to the reminder call.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Guest telephone is out of order.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Guest telephone is having continuous engaged or busy tone.</span></span></span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">One of the Front desk staff members to go along with the Security and knock the guest room to give the wakeup call.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">All such incidents should be recorded in detail on to the hotel log book / incident book.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Duty manager should check the&nbsp;wakeup call report&nbsp;and the same to be filed on a daily basis</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Training Summary Questions:</span></span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q1. What all details to be noted down while taking a wakeup call request.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q2. How can guest set a wakeup call by himself?</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q3. What is the importance of wakeup call in Front desk operations?</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q4. What to be done if guest doesn&#39;t answer the 2nd&nbsp;reminder call?</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n');
INSERT INTO `standard_operation_procedure_set_up` VALUES (23, '20 Releasing Tentative and Non- Guaranteed bookings', 1, '2019-10-03', 1, NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Date August 2019</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Releasing Tentative and Non- Guaranteed bookings</span></span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">SOP Number: FO - 21 (SOP serial number and department code)</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Department: Front Office - Reservation</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">While taking down the reservation the reservation agent should inform the guest / booker about the standard reservation holding time or release period as per the hotels policy.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Explain to booker or guest the following applicable reservation holding time.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">A non-Guaranteed reservation with no arrival or flight timing or bookings which are not re-confirmed shall be released by 1600hrs.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Reservation status will not be carried forward to the next day for a non-guaranteed booking; such bookings will be cancelled on the same day.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">A non-Guaranteed reservation with late night arrival or late night flight timing shall be released 4 hours after stated arrival/flight time on date of arrival.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">For Guaranteed reservation with credit-card details, Room will be blocked and checked in, or may be amended to the next day after reconfirming with the guest as per the availability.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">For guaranteed reservation no-show&rsquo;s the room will be checked in to charge retention charges and the same will be billed to the guest credit card.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">In case there is no pickup details or flight details giving by the guest, then request the Guest to call back once these details are available.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">For Guaranteed bookings (non &ndash; credit card) request the guest to make a deposit before arrival, As per hotel policy you can also give a cutoff date in case guests fail to make the deposit on or before the cut-off date the tentative reservation will be released.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Check with Booker / guest if any additional information or further assistance is required.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Note down the agreed reservation status (Guaranteed, non-guaranteed, credit card confirmed, deposit etc.) &nbsp;On the reservation form.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Information obtained must be repeated to ensure accuracy.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n');
INSERT INTO `standard_operation_procedure_set_up` VALUES (24, '21. Amending or Modifying Reservations', 1, '2019-10-03', 1, NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Date August 2019 </span></span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Amending or Modifying Reservations</span></span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">SOP Number:</span></span></strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;FO - 22 (SOP serial number and department code)</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Department:</span></span></strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;Front Office - Reservation</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">When guest / bookers call the hotel for amending existing reservation take down the original booking details on&nbsp;reservation form.(Guest Last name, Reservation no, Mobile etc.)</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Try to address the guest by name as and when possible.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Take permission from the guest to keep the call on hold.</span></span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Locate the original reservation record from the Property Management software.</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Reconfirm the booking details e.g.: guest name, arrival departure date, reservation number etc. with the guest again to make sure that you have&nbsp;</span></span></span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Retrieve the correct reservation record.</span></span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Enquire with the booker / guest on what amended to be made on the reservation. Eg: guest might like to extend the number of days of stay or want to shift his stay to another period.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Check the availability of both hotel and room type in the hotel software before confirming the amendment to the guest.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">If the guest had booked the original reservation on a best available rate or special packages, reservation agent should check the availability of these special rate and packages on this new date as well.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Any changes on rate or packages should be communicated to the guest.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">If the guest is shifting the arrival date to a different date then take down the new pickup / flight details. In case the guest requires a pick up form the hotel.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">All details have to be reconfirmed to the Guest.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Make the required amendments on the reservation record in system.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Valid payment method has to be taken for the new stay details.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Apply the Rate changes if any due to the amendment.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Generate a new confirmation letter to the guest which clearly shows the amended booking details.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Send the amended&nbsp;reservation confirmation letter&nbsp;to the guest email / fax.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Wish the guest a nice day ahead.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Training Summary questions:</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q1.&nbsp;What all details to note down when guest calls up the reservation department for amendment request?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q2. Is it required to reconfirm the pickup details in case there is a change in arrival date?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q3. What all need to be checked before confirming the amendment request to the guest?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q4. Can we offer the amended with the same rate of that of original booking?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q5. Is it required to send the guest amended confirmation letter?</span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n');
INSERT INTO `standard_operation_procedure_set_up` VALUES (25, '22. Booking inquiry for sold out dates', 1, '2019-10-03', 1, '2019-10-03', 1, '<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Date August 2019 </span></span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Booking enquiry for sold out dates</span></span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">SOP Number:</span></span></strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;FO - 23 (SOP serial number and department code)</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Department:</span></span></strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;Front Office - Reservation</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Very often in hotels reservation agents gets booking enquiry for dates when either the hotel is sold out or certain room types.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Note down guest name, mobile number, Arrival and departure date and room type requested by the guest on the&nbsp;reservation form.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Explain to the guest / booker that the hotel or the specific room type which they have requested for is fully booked.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Try to be transparent with the guest and provide the reasons for this. Eg: &quot;I am extremely sorry Mr. Ralph the hotel is fully booked on those dates there is a Doctor&rsquo;s conference held at the hotel&quot;</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Always provide alternate solution for regular guests, hotel loyalty program members, bookings from top company / travel agent accounts and those guests who was denied reservation earlier also.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">If the room type is sold out then:</span></span></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Explain other types of rooms which are available on the requested period.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Try and up sell higher category room to the guest.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">In case a of VIP guest or higher tier loyalty program member, then provide an upgrade to higher category.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Providing upgrade may depend as per hotel policy, in some case an approval from Front office manager / Reservation manger is required to approve an upgrade request.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">If Hotel is sold out then:</span></span></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Ask the guest if she would like to change the dates of the reservation to an alternative date.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Offer alternative date &quot;We would have room available from the 10th onwards&rdquo;</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Offer the choice of sister hotel in the city when possible or similar hotels of the same category.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Arrange for a reservation in the sister hotel if the guests is OK with this offer and confirm back to the guest once the reservation is made.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Take reservation on a waitlisted basis and assure the guest that we will get back to him as soon as there is an any availability due to cancellations or amendments.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Offer guest about details of other hotel alternative same level of service and pricing.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Offer any other assistance and ensure the guest of priority the next time.&nbsp;</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Make sure to record this enquire as turned down business or turn away after applying appropriate turn away reasons.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Wish the guest a nice day ahead.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Training Summary questions:</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q1. What all details to note down when guest calls up the reservation department?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q2. Is it required to inform the guest about the reason why the hotel is sold out?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q3. What can be done if room type is sold out?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q4. Steps to be taken when Hotel is sold out?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q5. Why it is important to record the turn away details correctly?</span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n');
INSERT INTO `standard_operation_procedure_set_up` VALUES (26, '22. On Queue or Wait Procedure', 1, '2019-10-03', 1, NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Date August 2019 </span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">On Queue or Wait Procedure</span></span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">SOP Number:</span></span></strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;FO - 24 (SOP serial number and department code)</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Department:</span></span></strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;Front Office &ndash; Guest Service</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">A guest &ldquo;On Queue&rdquo; or &ldquo;wait&rdquo; for room situation occurs when no ready rooms are available when a guest arrives at the hotel.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">The Front desk agent should change the status of the room as &quot;on queue&quot; or &quot;wait&quot; on the property management software and also inform the housekeeping department.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Housekeeping control desk can then priorities the room on queue immediately for cleaning and releasing for check-in.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">The Front desk agent should inform the guest of the situation and advise him/her as to the approximate length of time before the room is available.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">The Front desk staff can utilize this time to explain available services at the hotel to the guest, and also offer the guest a tour of the property. If there is any further delay due to any unforeseen&nbsp;issues then the same to be notified immediately to the guest.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">If the guest doesn&rsquo;t want to take the property tour then offer refreshments (Tea / Coffee / Juice) etc. and provide them with any required assistance.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">The guest&#39;s luggage should be safely stored by the Bellman and placed inside the room when available.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Never hand out keys to the blocked room to guest before it is ready; or indicate to the quest the anticipated room number.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">While the guest is waiting for the room to be ready, the normal registration procedure can be completed. And also inform the guest to return to the Front Desk to collect room key(s) when the room is ready.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">All guests who had waited for room need to be time stamped on registration card and the same need to be updated on the log book. &nbsp;</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Once the room blocked for the guest is made ready by the housekeeping, notify the Bell boy to deliver the guest&#39;s luggage to the room, and inform the guest about the same and also finalize processing of registration formalities if required.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">If the guest doesn&rsquo;t want to place the luggage automatically after the room is ready then the luggage slip needs to be attached to registration card to remind about the luggage kept with the Bell desk.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">When the guest returns for the ready room, the Guest service associate should escort him/her to the room.</span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">If large numbers of &ldquo;on Queue&rdquo; are anticipated like groups or conferences, the Front Office Manager or Assistant, at his/her discretion, may available to waiting guests a wash and change rooms. An alternative would be to utilize the Business Centre /Concierge Lounge/pool side area for this purpose.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Training Summary questions:</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q1.&nbsp;What does it mean by &ldquo;On Queue&rdquo;?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q2.&nbsp;Is it required to inform the guest about situation?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q3.&nbsp;What all can be done to keep the guest engaged while he / she is waiting for the room?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q4.&nbsp;Should the room key to be handed over to guest in advance?</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Tahoma&quot;,sans-serif\"><span style=\"color:#333333\">Q5.&nbsp;What can be done in case of a group waiting for room to be ready?</span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n');
INSERT INTO `standard_operation_procedure_set_up` VALUES (27, '24. How to Check guest car before pick-up', 1, '2019-10-03', 1, '2019-10-03', 1, '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">Date August 2019 </span></span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">SOP Number:</span></span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;FO - 25 (SOP serial number and department code)</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">Department:</span></span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">&nbsp;Front Office &ndash; Travel Desk / Concierge</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">Check Grooming &amp; Personal Hygiene of Drivers:</span></span></span></st